import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';

const AnalyticsChart = ({ authToken }) => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    if (authToken) {
      axios
        .get('http://localhost:5000/analytics', {
          headers: { Authorization: `Bearer ${authToken}` },
        })
        .then((response) => {
          const data = response.data; // Backend response
          const labels = data.map((item) => item.country); // Example: ['United States', 'India']
          const values = data.map((item) => item.activeUsers); // Example: [50000, 15000]

          setChartData({
            labels,
            datasets: [
              {
                label: 'Active Users by Country',
                data: values,
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                borderColor: 'rgba(75, 192, 192, 1)',
                borderWidth: 1,
              },
            ],
          });
        })
        .catch((error) => {
          console.error('Error fetching analytics data:', error);
        });
    }
  }, [authToken]);

  if (!chartData) return <div>Loading...</div>;

  return (
    <div style={{ width: '80%', margin: 'auto' }}>
      <Bar
        data={chartData}
        options={{
          responsive: true,
          plugins: {
            legend: {
              position: 'top',
            },
            title: {
              display: true,
              text: 'Active Users by Country',
            },
          },
        }}
      />
    </div>
  );
};

export default AnalyticsChart;
