import React, { useEffect, useState } from 'react';
import Chart from 'react-chartjs-2';
import { Chart as ChartJS, Title, Tooltip, Legend, LineElement, CategoryScale, LinearScale, PointElement } from 'chart.js';

ChartJS.register(Title, Tooltip, Legend, LineElement, CategoryScale, LinearScale, PointElement);

const AnalyticsChart = ({ authToken }) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchAnalyticsData = async () => {
      const token = localStorage.getItem('access_token') || authToken;

      if (token) {
        try {
          const res = await fetch('http://localhost:3000/api/analytics', {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          if (res.ok) {
            const analyticsData = await res.json();
            setData(analyticsData);  // Set the analytics data
          } else {
            // If the token is expired, try refreshing it
            const refreshRes = await fetch('http://localhost:3000/api/auth/refresh', { method: 'POST' });
            const { access_token } = await refreshRes.json();
            localStorage.setItem('access_token', access_token);
          }
        } catch (error) {
          console.error('Error fetching analytics data', error);
        }
      }
    };

    fetchAnalyticsData();
  }, [authToken]);

  if (!data) return <div>Loading...</div>;

  return (
    <div>
      <h2>Analytics Chart</h2>
      <Chart
        type="line"
        data={{
          labels: data.labels,
          datasets: [{
            label: 'Active Users',
            data: data.datasets,
            borderColor: 'rgba(75, 192, 192, 1)',
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
          }],
        }}
      />
    </div>
  );
};

export default AnalyticsChart;
